i = 10
print(f"nilai i sekarang --> {i}")

while i > 5:
    i -=1# nah dngn ini dia akan berhenti ktika angka ny sudah lebih kecil 5
    print(f"nilai i sekarang --> {i}")
    print("saya ganteng banget")

